import React from "react";
import "./Solo.css";
//-------------------------------------------------------------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------------------------------------------------------------
class Board extends React.Component {
  //   checkWinner = () => {
  //     let winLines = [
  //       ["0", "1", "2"],
  //       ["3", "4", "5"],
  //       ["6", "7", "8"],
  //       ["0", "3", "6"],
  //       ["1", "4", "7"],
  //       ["2", "5", "8"],
  //       ["0", "4", "8"],
  //       ["2", "4", "6"]
  //     ];
  //     for (let index = 0; index < winLines.length; index++) {
  //       const [a, b, c] = winLines[index];
  //       if (
  //         this.props.board[a] &&
  //         this.props.board[a] === this.props.board[b] &&
  //         this.props.board[a] === this.props.board[c]
  //       ) {
  //         this.props.gameHandle("win");
  //       } else if (!this.props.board.includes(null)) {
  //         this.props.gameHandle("tie");
  //       }
  //     }
  //   };

  computer = turn => {
    if (turn === 1) {
      let newBoard = this.props.board;
      if (newBoard[4] === null) {
        newBoard[4] = this.props.computer;

        this.setState({
          board: newBoard
        });
      } else {
        let boardArray = [];
        this.props.board.array.forEach(element => {
          if (element.value === null) {
            boardArray.push(element.index);
          }
        });
      }
    }
  };

  handleClick = index => {
    let newBoard = this.props.board;
    if (this.props.board[index] === null) {
      newBoard[index] = this.props.player;

      this.setState({
        board: newBoard,
        turn: this.props.turn + 1
      });
      this.computer(this.props.turn);
    }
    this.checkWinner();
  };
  render() {
    return (
      <div className="Board">
        {this.props.board.map((box, index) => (
          <div
            className="box"
            key={index}
            onClick={() => this.handleClick(index)}
          >
            {box}
          </div>
        ))}
      </div>
    );
  }
}

//-------------------------------------------------------------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------------------------------------------------------------

class SelectSymbol extends React.Component {
  handleSymbol = side => {
    console.log(side);
    this.props.symbolHandle(side);
  };

  render() {
    return (
      <div className="Select" style={{ display: `${this.props.display}` }}>
        <p>Select Player Symbol</p>
        <div className="symbol">
          <div className="box" onClick={() => this.handleSymbol("X")}>
            X
          </div>
          <div className="box" onClick={() => this.handleSymbol("O")}>
            O
          </div>
        </div>
      </div>
    );
  }
}
//-------------------------------------------------------------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------------------------------------------------------------
class Result extends React.Component {
  render() {
    return (
      <div className="result" style={{ display: `${this.props.display}` }}>
        {this.props.gameStatus === "win" ? (
          <h3>YAY!! Winnner. Wanna Play again?</h3>
        ) : this.props.gameStatus === "tie" ? (
          <h3>You Tied.</h3>
        ) : (
          <h3>You Lost. Try Again?</h3>
        )}
        <button
          onClick={() => {
            this.props.reset();
          }}
        >
          Play Again
        </button>
      </div>
    );
  }
}

//-------------------------------------------------------------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------------------------------------------------------------
//-------------------------------------------------------------------------------------------------------------------------------------------
class Solo extends React.Component {
  state = {
    board: Array(9).fill(null),
    gameCompleteState: "",
    player: "",
    computer: "",
    turn: 0,
    displaySelection: "",
    displayResult: "none"
  };
  symbolHandle = side => {
    if (side === "X") {
      this.setState({
        player: "X",
        computer: "O",
        displaySelection: "none"
      });
    } else if (side === "O") {
      this.setState({
        player: "O",
        computer: "X",
        displaySelection: "none"
      });
    }
  };

  gameHandle = gameState => {
    this.setState({
      gameCompleteState: gameState,
      displayResult: ""
    });
  };
  resetGame = () => {
    this.setState({
      board: Array(9).fill(null),
      gameCompleteState: "",
      player: "",
      turn: 0,
      displaySelection: "",
      displayResult: "none"
    });
  };
  render() {
    return (
      <div className="Solo">
        <SelectSymbol
          display={this.state.displaySelection}
          symbolHandle={this.symbolHandle}
        />
        <Board
          board={this.state.board}
          player={this.state.player}
          computer={this.state.computer}
          gameHandle={this.gameHandle}
          turn={this.state.turn}
        />
        <Result
          display={this.state.displayResult}
          gameStatus={this.state.gameCompleteState}
          winner={this.state.player}
          reset={this.resetGame}
          player={this.state.player}
        />
      </div>
    );
  }
}

export default Solo;
