import React from "react";

import "./App.css";

class Board extends React.Component {
  render() {
    return (
      <div className="Board">
        {this.props.board.map((box, index) => (
          <div className="box" key={index} onClick={e => this.handleClick}>
            {box}{" "}
          </div>
        ))}
      </div>
    );
  }
}

class SelectSymbol extends React.Component {
  render() {
    return (
      <div className="Select">
        <p>Select Player Symbol</p>
        <div className="symbol">
          <div className="box">X</div>
          <div className="box">O</div>
        </div>
      </div>
    );
  }
}

class App extends React.Component {
  state = {
    board: Array(9).fill("O"),
    gameCompleteState: false,
    playerSymbolSelect: false
  };

  render() {
    return (
      <div className="App">
        <SelectSymbol />
        <Board board={this.state.board} />
      </div>
    );
  }
}

export default App;
