import React, { Component } from "react";

import "./App.css";

const PlayerSelect = () => {
  return (
    <div className="playerSelection">
      <h4>Choose your Symbol</h4>
      <div className="symbols">
        <div className="side">
          <i class="far fa-circle" />
        </div>
        <div className="side">
          <i class="fas fa-times" />
        </div>
      </div>
    </div>
  );
};

const Board = props => {
  return (
    <div className="Board">
      <div className="box" id="1">
        {this.div.id}
      </div>
      <div className="box" id="2">
        {props.playerSymbol}
      </div>
      <div className="box" id="3">
        {props.playerSymbol}
      </div>
      <div className="box" id="4">
        {props.playerSymbol}
      </div>
      <div className="box" id="5">
        {props.playerSymbol}
      </div>
      <div className="box" id="6">
        {props.playerSymbol}
      </div>
      <div className="box" id="7">
        {props.playerSymbol}
      </div>
      <div className="box" id="8">
        {props.playerSymbol}
      </div>
      <div className="box" id="9">
        {props.playerSymbol}
      </div>
    </div>
  );
};

const Result = props => {
  return (
    <div>
      {props.winner ? (
        <div> YAY! You Won</div>
      ) : (
        <div> You lost. Better luck next time</div>
      )}
    </div>
  );
};

class App extends Component {
  state = {
    gameState: true,
    gameComplete: false,
    playerSymbol: "x",
    winner: ""
  };
  render() {
    return (
      <div className="App">
        {this.state.gameState ? (
          this.state.gameComplete ? (
            <Result winner={this.state.winner} />
          ) : (
            <Board symbol={this.state.playerSymbol} />
          )
        ) : (
          <PlayerSelect />
        )}
      </div>
    );
  }
}

export default App;
