import React from "react";

import "./App.css";

class Board extends React.Component {
  checkWinner = () => {
    let winLines = [
      ["0", "1", "2"],
      ["3", "4", "5"],
      ["6", "7", "8"],
      ["0", "3", "6"],
      ["1", "4", "7"],
      ["2", "5", "8"],
      ["0", "4", "8"],
      ["2", "4", "6"]
    ];
    for (let index = 0; index < winLines.length; index++) {
      const [a, b, c] = winLines[index];
      if (
        this.props.board[a] &&
        this.props.board[a] === this.props.board[b] &&
        this.props.board[a] === this.props.board[c]
      ) {
        console.log("winnner");
      }
    }
  };

  handleClick = index => {
    let newBoard = this.props.board;
    if (this.props.board[index] === null) {
      newBoard[index] = this.props.player;

      this.setState({
        board: newBoard
      });
      this.props.playerhandle();
    }
    this.checkWinner();
  };
  render() {
    return (
      <div className="Board">
        {this.props.board.map((box, index) => (
          <div
            className="box"
            key={index}
            onClick={() => this.handleClick(index)}
          >
            {box}{" "}
          </div>
        ))}
      </div>
    );
  }
}

class SelectSymbol extends React.Component {
  handleSymbol = e => {
    console.log(e.target.value);
    this.props.symbolHandle(e.target.value);
  };

  render() {
    return (
      <div className="Select">
        <p>Select Player Symbol</p>
        <div className="symbol" onClick={e => this.handleSymbol(e)}>
          <div className="box">X</div>
          <div className="box">O</div>
        </div>
      </div>
    );
  }
}

class App extends React.Component {
  state = {
    board: Array(9).fill(null),
    gameCompleteState: false,
    playerSymbolSelect: false,
    player: "",
    display: ""
  };
  symbolHandle = side => {
    this.setState({
      player: side,
      display: "none"
    });
  };
  playerhandle = () => {
    this.setState({
      player: this.state.player === "X" ? "O" : "X"
    });
  };
  render() {
    return (
      <div className="App">
        <SelectSymbol
          style={{ display: `${this.state.display}` }}
          symbolHandle={this.symbolHandle}
        />
        <Board
          board={this.state.board}
          player={this.state.player}
          playerhandle={this.playerhandle}
        />
      </div>
    );
  }
}

export default App;

