import React from "react";

import "./App.css";

class Board extends React.Component {
  handleClick = index => {
    let newBoard = this.props.board;

    newBoard[index] = this.props.player;
    let newPlayer = this.props.player === "X" ? "O" : "X";
    this.setState({
      board: newBoard,
      player: newPlayer
    });
  };
  render() {
    return (
      <div className="Board">
        {this.props.board.map((box, index) => (
          <div
            className="box"
            key={index}
            onClick={() => this.handleClick(index)}
          >
            {box}{" "}
          </div>
        ))}
      </div>
    );
  }
}

class SelectSymbol extends React.Component {
  render() {
    return (
      <div className="Select">
        <p>Select Player Symbol</p>
        <div className="symbol">
          <div className="box">X</div>
          <div className="box">O</div>
        </div>
      </div>
    );
  }
}

class App extends React.Component {
  state = {
    board: Array(9).fill(null),
    gameCompleteState: false,
    playerSymbolSelect: false,
    player: "X"
  };
  playerhandle = () => {
    let newPlayer = this.state.player === "X" ? "O" : "X";
    this.setState({
      player: newPlayer
    });
  };
  render() {
    return (
      <div className="App">
        <Board board={this.state.board} player={this.playerhandle} />
      </div>
    );
  }
}

export default App;

