import React, { Component } from "react";

import "./App.css";

const PlayerSelect = () => {
  return (
    <div className="playerSelection">
      <h4>Choose your Symbol</h4>
      <div className="symbols">
        <div className="side">
          <i class="far fa-circle" />
        </div>
        <div className="side">
          <i class="fas fa-times" />
        </div>
      </div>
    </div>
  );
};

const Containers = props => {
  const handleClick = () => {};
  return (
    <div className="containers">
      <div
        className="box"
        id="1"
        onClick={() => this.handleClick(props.symbol)}
      />
      <div className="box" id="2" />
      <div className="box" id="3" />
      <div className="box" id="4" />
      <div className="box" id="5" />
      <div className="box" id="6" />
      <div className="box" id="7" />
      <div className="box" id="8" />
      <div className="box" id="9" />
    </div>
  );
};

class App extends Component {
  state = {
    gameState: true,
    gameComplete: false,
    playerSymbol: "x"
  };
  render() {
    return (
      <div className="App">
        {this.state.gameState ? (
          <Containers symbol={this.state.playerSymbol} />
        ) : (
          <PlayerSelect />
        )}
      </div>
    );
  }
}

export default App;
